"use client";

import { RotationPreview } from "@/components/rotation/RotationPreview";
import type { Rotation } from "@/types/rotation";

const sampleRotation: Rotation = {
  id: "rotation-1",
  title: "Boss Burst Rotation",
  footerNote:
    "To perform Combo Reversal, press both Combo Skills in quick succession.",
  steps: [
    {
      id: "step-1",
      character: {
        id: "chara-1",
        name: "Operator A",
        image: "/characters/a.png",
      },
      action: "skill",
      trigger: { type: "tap" },
    },
    {
      id: "step-2",
      character: {
        id: "chara-2",
        name: "Operator B",
        image: "/characters/b.png",
      },
      action: "combo",
      trigger: { type: "multi_tap", count: 2 },
      transition: {
        label: "Combo Reversal",
      },
    },
    {
      id: "step-3",
      character: {
        id: "chara-3",
        name: "Operator C",
        image: "/characters/c.png",
      },
      action: "ultimate",
      trigger: { type: "hold" },
    },
  ],
};

export default function Page() {
  return (
    <main className="min-h-screen bg-slate-950 flex flex-col items-center justify-center gap-8 p-10">

      {/* Rotation Preview */}
      <RotationPreview rotation={sampleRotation} />

      {/* Action Buttons (placeholder) */}
      <div className="flex gap-4">
        <button
          className="
            px-6 py-2
            rounded-lg
            bg-blue-600
            hover:bg-blue-500
            text-white
            font-medium
          "
        >
          Download Image
        </button>

        <button
          className="
            px-6 py-2
            rounded-lg
            bg-slate-700
            hover:bg-slate-600
            text-white
            font-medium
          "
        >
          Edit Rotation
        </button>
      </div>
    </main>
  );
}
