"use client";

import { Rotation, RotationStep, ActionType, Trigger, Transition, Character } from "@/types/rotation";
import { Plus, Trash } from "lucide-react";

interface BuilderPanelProps {
  rotation: Rotation;
  onChange: (rotation: Rotation) => void;
}

const ACTIONS: ActionType[] = ["basic", "skill", "ultimate", "combo", "final"];
const TRIGGER_TYPES = ["tap", "hold", "multi_tap", "until_combo"] as const;
const TRANSITIONS: Transition[] = [{ label: "quick" }, { label: "delay" }, { label: "swap" }];

export function BuilderPanel({ rotation, onChange }: BuilderPanelProps) {
  function updateStep(index: number, patch: Partial<RotationStep>) {
    const steps = [...rotation.steps];
    steps[index] = { ...steps[index], ...patch };
    onChange({ ...rotation, steps });
  }

  function addStep() {
    const newStep: RotationStep = {
      id: crypto.randomUUID(),
      character: {
        id: "unknown",
        name: "Unknown",
        image: "/characters/placeholder.png",
      },
      action: "basic",
      trigger: { type: "tap" }, // object sesuai type Trigger
    };
    onChange({ ...rotation, steps: [...rotation.steps, newStep] });
  }

  function removeStep(index: number) {
    onChange({
      ...rotation,
      steps: rotation.steps.filter((_, i) => i !== index),
    });
  }

  return (
    <aside className="w-[360px] bg-slate-900 border border-white/10 rounded-xl p-4 flex flex-col gap-4">
      <h2 className="text-lg font-semibold">Rotation Builder</h2>

      <div className="flex flex-col gap-3 overflow-y-auto max-h-[80vh] pr-1">
        {rotation.steps.map((step, index) => (
          <div
            key={step.id}
            className="bg-slate-800 rounded-lg p-3 flex flex-col gap-2"
          >
            <div className="flex justify-between items-center">
              <span className="text-sm font-medium">Step {index + 1}</span>
              <button
                onClick={() => removeStep(index)}
                className="text-red-400 hover:text-red-300"
              >
                <Trash size={16} />
              </button>
            </div>

            {/* Action */}
            <select
              value={step.action}
              onChange={(e) =>
                updateStep(index, { action: e.target.value as ActionType })
              }
              className="bg-slate-900 border border-white/10 rounded px-2 py-1 text-sm"
            >
              {ACTIONS.map((a) => (
                <option key={a} value={a}>
                  {a}
                </option>
              ))}
            </select>

            {/* Trigger */}
            <select
              value={step.trigger.type} // pakai .type
              onChange={(e) => {
                const type = e.target.value as Trigger["type"];
                let trigger: Trigger;
                if (type === "multi_tap") {
                  trigger = { type, count: 2 }; // default count
                } else {
                  trigger = { type };
                }
                updateStep(index, { trigger });
              }}
              className="bg-slate-900 border border-white/10 rounded px-2 py-1 text-sm"
            >
              {TRIGGER_TYPES.map((t) => (
                <option key={t} value={t}>
                  {t}
                </option>
              ))}
            </select>

            {/* Transition */}
            <select
              value={step.transition?.label ?? ""}
              onChange={(e) =>
                updateStep(index, {
                  transition: e.target.value ? { label: e.target.value } : undefined,
                })
              }
              className="bg-slate-900 border border-white/10 rounded px-2 py-1 text-sm"
            >
              <option value="">No Transition</option>
              {TRANSITIONS.map((t) => (
                <option key={t.label} value={t.label}>
                  {t.label}
                </option>
              ))}
            </select>
          </div>
        ))}
      </div>

      <button
        onClick={addStep}
        className="mt-auto flex items-center justify-center gap-2 bg-indigo-600 hover:bg-indigo-500 rounded-lg py-2 text-sm font-medium"
      >
        <Plus size={16} /> Add Step
      </button>
    </aside>
  );
}
