import { Rotation } from "@/types/rotation";
import { StepCard } from "./StepCard";
import { TransitionArrow } from "./TransitionArrow";
import { FooterNote } from "./FooterNote";

interface RotationPreviewProps {
  rotation: Rotation;
}

export function RotationPreview({ rotation }: RotationPreviewProps) {
  return (
    <div
      id="rotation-preview"
      className="
        w-[1600px]
        h-[600px]
        bg-gradient-to-br
        from-slate-900
        to-slate-800
        text-white
        p-16
        flex
        flex-col
        justify-between
        rounded-2xl
      "
    >
      {/* Title */}
      <div className="mb-10">
        <h1 className="text-3xl font-bold tracking-wide">
          {rotation.title}
        </h1>
      </div>

      {/* Steps Section */}
      <div className="flex-1 flex items-center justify-center">
        <div className="flex items-center gap-12">
          {rotation.steps.map((step, index) => (
            <div key={step.id} className="flex items-center gap-12">
              
              {/* Step Card */}
              <StepCard step={step} />

              {/* Arrow (not rendered on last step) */}
              {index < rotation.steps.length - 1 && (
                <TransitionArrow
                  label={step.transition?.label}
                />
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Footer */}
      <FooterNote text={rotation.footerNote} />
    </div>
  );
}
